var util = require('util');

function Error(str) {
    this._str = str;
    Error.count++;
}
Error.count = 0;
Error.prototype.getInfo = function () {
    console.log('str = ' + this._str);
    console.log('count errors = ' + Error.count);
};
Error.getError = function (str) {
    return str;
};
module.exports = Error;
//-------------------------DragAndDrop
function DragAndDrop(str) {
    DragAndDrop.super_.apply(this, arguments);
    DragAndDrop._count++;
}
DragAndDrop._count = 0;
DragAndDrop.prototype.getInfo = function () {
    Error.prototype.getInfo.apply(this, arguments);
    console.log('count DragAndDrop errors = ' + DragAndDrop._count);
};

util.inherits(DragAndDrop, Error);
